INSERT INTO `tbl_comments` (`comments_id`, `blog_id`, `comments_author_name`, `email_address`, `comments`, `publication_status`, `comments_date_time`) VALUES
(1, 21, 'Rasel', 'rasel24@gmail.com', 'স্কোরশিটে শেষ পর্যন্ত ইব্রাহিমোভিচের নাম উঠেছে যোগ করা সময়ে—পেনাল্টি থেকে গোল করে।', 1, '2017-04-11 09:13:30'),
(2, 19, 'Nazmul', 'raseln90@gmail.com', 'আজ মঙ্গলবার বিকেলে নয়াপল্টনে দলের কেন্দ্রীয় কার্যালয়ে এক সংবাদ সম্মেলনে মির্জা ফখরুল এসব কথা বলেন।', 0, '2017-04-11 09:13:30'),
(3, 17, 'Sad', 'sat78@gmail.com', 'কাল বাংলাদেশ দলের হয়ে ৫৩তম টি-টোয়েন্টি ম্যাচ খেলেছেন মাশরাফি। অধিনায়ক হিসেবে এটি ছিল তাঁর ২৭তম ম্যাচ।', 1, '2017-04-11 09:13:30'),
(4, 17, 'Nazmul', 'haque@gmail.com', 'শ্রীলঙ্কার বিপক্ষে এবারের টেস্ট সিরিজ চলার সময়ই জানা গিয়েছিল, কোচ চন্ডিকা হাথুরুসিংহে সীমিত ওভারের ক্রিকেটে মাশরাফিকে আর চাচ্ছেন না। বিশেষ করে, টি-টোয়েন্টির জন্য কোচের যে টিম কম্বিনেশন, সেখানে মাশরাফির জায়গা হয় না। তাঁর বোলিংটাই নাকি হাথুরুসিংহের কাছে টি-টোয়েন্টি উপযোগী মনে হচ্ছে না। এই সংস্করণের ক্রিকেটের জন্য তাই বিসিবির কাছে নতুন অধিনায়কও চেয়ে পাঠিয়েছিলেন তিনি শ্রীলঙ্কা থেকে। ওয়ানডে সিরিজের আগে মাশরাফি তাই অস্বস্তি নিয়েই যোগ দিয়েছিলেন দলের সঙ্গে।', 0, '2017-04-11 09:13:30'),
(5, 15, 'Haqu', 'haque99@gmail.com', 'গত বছর প্রেসিডেন্ট নির্বাচনে পুতিনের হস্তক্ষেপ নিয়ে মার্কিন কংগ্রেসে তদন্ত চলছে। যুক্তরাষ্ট্রের গোয়েন্দা সংস্থাগুলো অভিযোগ করেছে, ট্রাম্পকে জেতাতে পুতিন নির্বাচনে সহায়তা করেছেন।', 1, '2017-04-11 09:13:30'),
(6, 19, 'Haqu', 'rasel@info.com', 'গণমাধ্যমে প্রকাশিত খবরের বরাত দিয়ে বিএনপির মহাসচিব বলেন, প্রতিরক্ষার নামে ভারত বাংলাদেশের সঙ্গে ৫০ কোটি ডলার বা চার হাজার কোটি টাকার ঋণচুক্তি করবে। এই টাকা দিয়ে ভারত থেকেই অস্ত্র কিনতে হবে। স্টকহোম ইন্টারন্যাশনাল পিস রিসার্চ ইনস্টিটিউটের মতে, ১৯৫০ সালের পর অস্ত্র আমদানিতে এক নম্বরে আছে ভারত। যে দেশ নিজেই অস্ত্র আমদানিতে এক নম্বর, সে দেশের কাছ থেকে বাংলাদেশ কেন অস্ত্র কিনতে যাবে?', 1, '2017-04-11 09:13:30'),
(7, 14, 'Nazmul Haque', 'raseln9@gmail.com', 'বৈঠকে নতুন মোটরযান খসড়া আইন অমান্য করার জন্য প্রচুর জরিমানার ব্যাপারটি নিয়ে আলোচনা হয়েছে। নিয়ম অমান্য করলে জরিমানা কমানোর জন্য আন্দোলনে যেতে চায় মালিকপক্ষ। তারা এ ব্যাপারে প্রধানমন্ত্রীর সঙ্গে সাক্ষাতের চেষ্টা করবেন।', 0, '2017-04-11 09:15:36'),
(8, 20, 'tttttu', 'raseln90@gmail.com', 'iiiii', 0, '2017-05-14 04:38:02');

